insert into `#__content` values ('24', 'Contact Staff', '', '<h2>The Staff at Christ Church</h2> <p>Pastor:&nbsp; Jerry Eslinger</p><p>Director of Christian Education: Jeremy Shaver</p><p>Secretary: (open position)</p><p>Book Keeper: Sidney Nelson&nbsp;</p><p>Director of the Chancel Choir and Bell Choir: Liana Lansing</p><p>Nursery Attendant: Allyson Morris </p>  ', '<h3> <a href=\"index.php?option=com_contact&amp;catid=12&amp;Itemid=3\">Contact Us </a></h3><p>As a staff, we are committed to serve the church and its mission using the various gifts and talents with which God has endowed us. Our hope is that our ministries within our building may empower our membership for our ministries outside - in our work, our families and our community. </p>', '1', '6', '0', '21', '2007-04-18 21:43:43', '62', '', '2007-08-31 17:53:06', '62', '0', '0000-00-00 00:00:00', '2007-04-18 21:38:26', '0000-00-00 00:00:00', 'pewbuddy.jpg|||0||bottom||', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=0\ncreatedate=0\nmodifydate=\npdf=0\nprint=\nemail=\nkeyref=\ndocbook_type=', '8', '0', '1', 'phone, email, contact, staff', 'Contact information for the church', '0', '1');
insert into `#__content` values ('23', 'Worship Times', '', '<h2>Fall, Winter, and Spring:</h2> <h4>Adult Study: 9:00 am (nursery provided)<br /> Worship Service: 10:00 am (nursery provided)<br /> Children&#39;s Sunday School: 10:15 am (after youth sermon during worship service)<br /></h4>  <h2>Summer (June 3 - Aug 26):</h2> <h4>Worship Service: 9:30 am (nursery provided)</h4><ul class=\"arrows\"><li><a href=\"index.php?option=com_content&amp;task=view&amp;id=41&amp;Itemid=34\">Worship Service Details</a></li></ul><p>&nbsp;</p>', '<h3>All are welcome at Christ Congregational Church!</h3>  <br /> {mosimage}   <strong><span class=\"note\">I am a Pew Buddy sitting here for you! A friend to share the service,  a hug when feeling blue. A friendly face for children and adults alike. Please enjoy my company anytime you&#39;d like. Just remember in the end to leave me in the pew, so next week I&#39;ll be here, waiting just for you.</span></strong>  The members of our church want you to be comfortable.  Come as you are.  ', '1', '6', '0', '19', '2007-04-18 21:43:43', '62', '', '2007-08-31 17:09:13', '62', '0', '0000-00-00 00:00:00', '2007-04-18 21:38:26', '0000-00-00 00:00:00', 'pewbuddy.jpg|||0||bottom||', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=0\ncreatedate=0\nmodifydate=\npdf=0\nprint=\nemail=\nkeyref=\ndocbook_type=', '14', '0', '1', 'Worship, services, time, schedule', 'Worship Schedule.', '0', '1');
insert into `#__content` values ('25', 'Mission Statement', '', '{mosimage} Christ Congregational Church is a progressive, caring, and celebrating Christian congregation of the United Church of Christ. We are a serving, learning, and worshipping community of faith. We welcome and care for one another. We desire to be diverse, justice-promoting, tradition-affirming, future-directed, and multi-generational. The focus of our ministry is on our neighborhood and the larger Metro area, with ongoing attention to the needs of the global community. <hr />', '', '1', '0', '0', '0', '2007-04-20 18:23:11', '62', '', '2007-07-30 22:43:41', '62', '0', '0000-00-00 00:00:00', '2007-04-20 18:17:50', '0000-00-00 00:00:00', 'ucc_logo.gif|left|ucc logo|0||bottom||', '', 'menu_image=-1\nitem_title=1\npageclass_sfx=\nback_button=\nrating=0\nauthor=0\ncreatedate=0\nmodifydate=\npdf=\nprint=\nemail=', '1', '0', '1', '', '', '0', '0');
insert into `#__content` values ('26', 'How to Become a Member', '', '<h4>Join Your Journey to Ours.... \r\n\r\n <span class=\"note\">Dear Friends,\r\nI want to personally invite you to visit our congregation and join our church family. We offer you a warm welcome and a place for you to grow in your spiritual pilgrimage. All age groups have opportunities available.  <em> - Rev. Jerry Eslinger</em></span>\r\n<a href=\"http://visitchristchurch.org/index.php?option=com_contact&task=view&contact_id=2&Itemid=3\">Contact the church office</a> to speak to Jerry about membership.\r\n', '<br /><br />\r\nWords of Welcome:\r\nWe welcome you with joy in the common life of this church. We promise you our friendship and prayers as we share the hopes and labors of the church of Jesus Christ. By the power of the Holy Spirit may we continue to grow together in God\'s knowledge and love and be witnesses of our risen Savior.</h4>\r\n', '1', '6', '0', '22', '2007-04-18 21:43:43', '62', '', '2007-04-22 23:09:22', '62', '0', '0000-00-00 00:00:00', '2007-04-18 21:38:26', '0000-00-00 00:00:00', 'pewbuddy.jpg|||0||bottom||', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=0\ncreatedate=0\nmodifydate=\npdf=0\nprint=\nemail=\nkeyref=\ndocbook_type=', '9', '0', '1', 'join, membership, member', 'Information about becoming a member', '0', '0');
insert into `#__content` values ('105', 'Sanctuary Cross', '', '<p>{mosimage}&nbsp; This beautiful cross hangs in our sanctuary.&nbsp; It was created by local Denver artist and church member, Robert Wogrin.</p><p>Bob explains that the concept for the design of this cross was already in his mind.&nbsp; He patterned four different sizes of crosses and they were cut out by all the members of the congregation.&nbsp; The pieces were sodered into random patterns for rows deep.&nbsp; </p><p>&quot;Each cross is a symbol for all Christians, from the time of Christ, who have been failthful and loyal to his teachings&quot;, Bob continues.&nbsp; &quot;The cross stands not only for his sacrifice but also for his followers.&nbsp; I feel that I built the cross - however I do not believe I designed it&quot;.&nbsp;</p> {mosimage}', '', '1', '17', '0', '43', '2007-07-30 23:03:20', '62', '', '2007-09-03 18:43:38', '62', '0', '0000-00-00 00:00:00', '2007-07-30 22:52:30', '0000-00-00 00:00:00', 'cross3.jpg\r\nCrossSunburst.jpg', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=', '7', '0', '2', '', '', '0', '2');
