insert into `#__content` values ('96', 'July Mission of the Month: Colorado Christian Home, Tennyson Center for Children and Families', '', '<span style=\"font-size: 12pt; font-weight: normal; font-style: normal\">Our church&rsquo;s July mission offering will support the Colorado Christian Home, Tennyson Center for Children and Families.<span>&nbsp; </span>This center has given hope to thousands of children.<span>&nbsp; </span>Tennyson Center is a community of people dedicated to breaking the cycle abuse and neglect, serving children, and strengthening families.<span>&nbsp; </span>As professional and concerned citizens, they strive to provide competent and caring treatment, educational, and advocacy services that are community-based, family-focused, and child-centered. </span>  ', '<span style=\"font-size: 12pt; font-weight: normal; font-style: normal\">A few services they offer are an accredited program designed to meet the academic and behavioral needs of students, a program for intensive short-term assessments for children, and a 24- hour treatment for troubled children.<span>&nbsp; </span>The program we support with our mission offering is called &ldquo;Dress A Child&rdquo;.<span>&nbsp; </span>As school approaches in August, the goal is to be able to buy some new school clothes and shoes for each of the children who live on campus.<span>&nbsp; </span>We all know how good it feels when we have new things to wear.<span>&nbsp; </span>The Mission/Outreach Board asks for support of this valuable organization.</span>', '1', '14', '0', '29', '2007-06-30 00:29:33', '62', '', '2007-08-06 23:10:59', '62', '0', '0000-00-00 00:00:00', '2007-06-30 00:27:27', '2007-09-28 02:00:00', '', '', '', '2', '0', '3', '', '', '0', '0');
