insert into `#__content` values ('107', 'Congregational Meeting Considers By-Laws Changes', '', '<strong></strong>  <p class=\"MsoNormal\"><span style=\"font-family: Arial\">All Christ Congregational Church members are invited to a special congregational meeting after worship on <strong>Sunday, August 26<sup>th</sup></strong> to consider the proposed by-laws.<span>&nbsp; </span>The by-laws were mailed to members for review.<span>&nbsp; </span>Please bring them with you to the meeting.<span>&nbsp; </span>The revision of our church by-laws is one of the recommendations from the long-range plan adopted by the congregation earlier this year.<span>&nbsp; </span>Your comments, suggestions and questions are welcomed.</span></p>  <p class=\"MsoNormal\"><span style=\"font-size: 8pt; font-family: Arial\">&nbsp;</span></p>  <p style=\"text-align: center\" class=\"MsoNormal\" align=\"center\">&nbsp;</p>  ', '', '-2', '1', '0', '1', '2007-08-04 23:47:06', '62', '', '2007-08-04 23:50:26', '62', '0', '0000-00-00 00:00:00', '2007-08-04 23:45:17', '2007-08-26 02:00:00', '', '', '', '2', '0', '4', '', '', '0', '0');
insert into `#__content` values ('109', 'Community Ministry Canning Project', '', '  <p class=\"MsoNormal\"><span style=\"font-family: Arial\">Volunteers are needed to work at the Cannery on August 8 and August 29.<span>&nbsp; </span>The food canned by CM volunteers is given to the CM food bank and helps reduce its expenses.<span>&nbsp; </span>We will car pool from the Ministry at 8:15 a.m.<span>&nbsp; </span>You must be at least 16 years old.<span>&nbsp; </span>Talk with Karen Bickett or Jerry Eslinger if you are interested.</span></p>  ', '', '-2', '1', '0', '1', '2007-08-04 23:53:19', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2007-08-04 23:52:14', '2007-08-29 02:00:00', '', '', '', '1', '0', '3', '', '', '0', '0');
insert into `#__content` values ('108', 'Community Ministry Food Bank', '', '  <div style=\"border: 1pt solid windowtext; padding: 0in 4pt 1pt\">  <p style=\"border: medium none ; padding: 0in; text-align: center\" class=\"MsoNormal\" align=\"center\"><strong><span style=\"font-family: Arial\">Community Ministry Food Bank</span></strong><span style=\"font-family: Arial\"></span></p>  <p style=\"border: medium none ; padding: 0in; text-align: center\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family: Arial\">They have asked for our church to donate <strong>RICE</strong> for the month of August.</span></p>  <p style=\"border: medium none ; padding: 0in; text-align: center\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family: Arial\">Of course, any other donations (food, children&rsquo;s clothes, children&rsquo;s </span></p>  <p style=\"border: medium none ; padding: 0in; text-align: center\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family: Arial\">undergarments, personal care items) are always appreciated.</span></p>  </div>  ', '', '-2', '1', '0', '1', '2007-08-04 23:49:56', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2007-08-04 23:49:09', '2007-08-31 02:00:00', '', '', '', '1', '0', '2', '', '', '0', '0');
insert into `#__content` values ('110', 'Fall Kick-Off Sunday Slated For September 16', '', '<strong></strong>  <p class=\"MsoNormal\"><span style=\"font-family: Arial; color: black\">All Christ Congregational Church members and friends are invited to the start of our new church year and fall program on <strong>Sunday, September 16</strong>. </span></p>  ', '<span style=\"font-family: Arial; color: black\">Our education programs for children, youth, and adults will resume that morning and our Chancel Choir and Handbell Choir will return to lead us in worship through inspiring music. As a special treat, the Chancel Choir and Owd Dabar &ndash; our contemporary music ensemble &ndash; will offer a special anthem. We will gather for worship at 10 a.m. and then enjoy lunch on the church patio and grounds. The children and youth will serve up hot dogs, hamburgers, potato salad, chips, watermelon and cookies. You are also invited to participate in some games, including volleyball. Plan now to join us for this exciting event and invite a friend!</span>', '-2', '14', '0', '30', '2007-08-04 23:59:56', '62', '', '2007-09-03 19:02:24', '62', '0', '0000-00-00 00:00:00', '2007-08-04 23:58:29', '2007-09-17 02:00:00', '', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=', '2', '0', '0', '', '', '0', '0');
insert into `#__content` values ('111', 'August Mission of the Month: Christ Church Foundation', '', 'A group of Christ Church members began meeting in 1998 to form the Foundation and to develop a life-income planned gift opportunity for church members and friends.&nbsp; The Foundation is an invaluable asset and provides critical support of our church&#39;s mission to its members and the greater community.', '', '1', '14', '0', '29', '2007-08-06 23:09:40', '62', '', '2007-08-06 23:10:26', '62', '0', '0000-00-00 00:00:00', '2007-08-06 23:06:15', '0000-00-00 00:00:00', '', '', '', '2', '0', '2', '', '', '0', '0');
insert into `#__content` values ('112', 'Homelessness Project Update - August 2007', '', '<span style=\"font-family: Arial\">Our new family has arrived!&nbsp; We have just been assigned a new family to mentor through the Family and Senior Homelessness Initiative (FSHI).&nbsp;&nbsp;Tricia is a single mother of two daughters ages 12 and 1 and is employed (we are told - we haven&#39;t met Tricia yet!) as a phlebotomist in Lakewood, CO. She has been living with family and friends but looks forward to having a home for herself and her daughters with just a little bit of help from our congregation and from FSHI.</span><p class=\"MsoNormal\"><span style=\"font-family: Arial\"></span></p>  ', '<p class=\"MsoNormal\"><span style=\"font-family: Arial\">We are delighted to have three new volunteers.&nbsp;Virginia Black, Mim Willyard and Carolyn Anderson&nbsp;will&nbsp;join our mentor group and receive training.&nbsp;FSHI is coordinating with us to try to schedule training on the same day as, and just prior to, our first meeting with this new family.</span></p>   <p class=\"MsoNormal\"><span style=\"font-family: Arial\">Sandy Maben has attended the first of what is expected to be a quarterly roundtable gathering of&nbsp;mentor teams from churches that are participating in the FSHI program across the metro area. The group shares a meal as well as stories, victories, frustrations, common experiences and questions on what we are doing. It is a wonderful way to keep up with the total impact that the program is having.</span></p>      <p class=\"MsoNormal\"><span style=\"font-family: Arial\">Please see a member of the mentoring committee if you have any suggestion or ideas that will support our work with the families.</span></p>', '1', '10', '0', '41', '2007-08-31 16:19:16', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2007-08-31 16:16:57', '0000-00-00 00:00:00', '', '', '', '1', '0', '1', 'Family and Senior Homelessness Initiative (FSHI)', 'Family and Senior Homelessness Initiative (FSHI)Update', '0', '0');
insert into `#__content` values ('113', 'New Bylaws Create Seven Ministries', '', '<strong></strong><span style=\"font-family: Arial\">The new Christ Congregational Church bylaws were approved at the August 26 congregational meeting. So, what is the next step?</span>  <p class=\"MsoNormal\"><span style=\"font-family: Arial\">Many of our existing church boards will become ministry teams and new ministries will be created. All existing boards will retain responsibility until the new bylaws go into effect on Sunday, September 30. The Nominating Committee, composed of Vice-Moderator Sandy Selzer, at-large council members Janet Burns and Marve Evers, and the Rev. Jerry Eslinger, has begun recruiting and encouraging individuals to serve as coordinators of the new ministries. These coordinators will be elected by the congregation.</span></p>', '<p class=\"MsoNormal\"><span style=\"font-family: Arial\">Do you agree that we all are responsible for making these changes work? Is it really true that 20 percent of the people do 80 percent of the work? Consider asking yourself, &ldquo;How am I involved in the activities of Christ Church? Am I part of the 20 percent or the 80 percent?&rdquo;</span></p>   <span style=\"font-size: 12pt; font-family: Arial\">Our first ministry coordinators will play an important role in charting the church&rsquo;s path over the next seven months &ndash; until the next Annual Meeting in May. We need a dedicated team of ministry coordinators and ministry volunteers. Would you be willing to serve?<span>&nbsp; </span>We invite you all to be a part of this new day for Christ Church!<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>The Nominating Committee</span>', '1', '1', '0', '1', '2007-08-31 16:24:15', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2007-08-31 16:23:07', '2007-09-30 02:00:00', '', '', '', '1', '0', '9', '', '', '0', '0');
insert into `#__content` values ('114', 'Community Ministry Food Bank - September', '', '  <div style=\"border: 1pt solid windowtext; padding: 1pt 4pt\">  <p style=\"border: medium none ; padding: 0in; text-align: center\" class=\"MsoNormal\" align=\"center\"><strong><span style=\"font-family: Arial\">Community Ministry Food Bank</span></strong></p>    <p style=\"border: medium none ; padding: 0in; text-align: center\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family: Arial\">They have asked for our church to donate <strong>Canned</strong> <strong>Soup</strong> for the month of September.<span>&nbsp; </span>Of course, any other donations (food, children&rsquo;s clothes, children&rsquo;s undergarments, personal care items) are always appreciated.</span></p>  </div>  <p style=\"text-align: center\" class=\"MsoNormal\" align=\"center\">&nbsp;</p>  ', '', '1', '7', '0', '39', '2007-08-31 16:26:50', '62', '', '2007-08-31 16:39:28', '62', '0', '0000-00-00 00:00:00', '2007-08-31 16:26:03', '2008-08-31 02:00:00', '', '', '', '2', '0', '1', '', '', '1', '0');
insert into `#__content` values ('115', 'Chancel Choir and Bell Choir Resume', '', '<span style=\"font-family: Arial\">Regular Chancel Choir rehearsals will resume on THURSDAY, SEPTEMBER 13, at 7:00 p.m. &ndash; 8:30 (usually).<span>&nbsp; </span>For our kick-off Sunday, September 16<sup>th</sup>, both the Chancel and Owd Dabar singers will join in the rousing song, &ldquo;Shout, Amen!&rdquo; which we&rsquo;ll rehearse together on Thursday from 7-7:30.</span>    <p class=\"MsoNormal\"><span style=\"font-family: Arial\">The Bell Choir will begin its regular rehearsals on SUNDAY, SEPT 16<sup>th</sup> at 11:30 a.m. in the sunshine room (1<sup>st</sup> floor, west side, past the library and nursery.)</span></p>    <p class=\"MsoNormal\"><span style=\"font-family: Arial\"><br /></span></p>  ', '<span style=\"font-family: Arial\">Both Chancel and Bell Choirs warmly welcome new or &ldquo;returning&rdquo; singers and ringers.<span>&nbsp; </span>Altho&rsquo; being able to read music is helpful, it is NOT a prerequisite&mdash;nor is there any age restrictions.<span>&nbsp; </span>Please come and help us make a joyful noise!</span>', '1', '1', '0', '1', '2007-08-31 16:29:47', '62', '', '2007-09-03 21:36:56', '62', '0', '0000-00-00 00:00:00', '2007-08-31 16:28:41', '2007-09-28 02:00:00', '', '', '', '2', '0', '8', '', '', '0', '0');
