insert into `#__content` values ('129', 'Fall Kick-Off Sunday Slated For September 16', '', '<span style=\"font-family: Arial; color: black\">All Christ Congregational Church members and friends are invited to the start of our new church year and fall program on Sunday, September 16. Our education programs for children, youth, and adults will resume that morning and our Chancel Choir and Handbell Choir will return to lead us in worship through inspiring music. </span>  ', '<span style=\"font-family: Arial; color: black\">As a special treat, the Chancel Choir and Owd Dabar &ndash; our contemporary music ensemble &ndash; will offer a special anthem. We will gather for worship at 10 a.m. and then enjoy lunch on the church patio and grounds. The children and youth will serve up hot dogs, hamburgers, potato salad, chips, watermelon and cookies. You are also invited to participate in some games, including volleyball. Plan now to join us for this exciting event and invite a friend!</span>', '1', '14', '0', '30', '2007-09-03 19:03:22', '62', '', '2007-09-03 19:10:13', '62', '0', '0000-00-00 00:00:00', '2007-09-03 19:02:30', '2007-09-17 02:00:00', '', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=', '2', '0', '1', '', '', '0', '1');
insert into `#__content` values ('130', 'Interfaith Music Festival Aims To “Turn The World Around”', '', '<strong></strong><span style=\"font-family: Arial; color: black\">The Seventh Annual Interfaith Music Festival sponsored by The Interfaith Alliance of Colorado is set for 7 p.m. <strong>Sunday, October 14</strong> at Montview Boulevard Presbyterian Church, 1980 Dahlia St. in Denver. The theme for this year&rsquo;s concert is &ldquo;Turn the World Around.&rdquo; </span>  ', '<span style=\"font-family: Arial; color: black\">The festival will feature performers from a variety of faith traditions, including:<span>&nbsp; </span>Los Lantzmun, a group that plays a lively combination of Jewish and Sufi music; Alandi Ashram of the Boulder Kirtan, who will raise his voice in Hindu chanting; and the Sojourners, and ensemble of The Spirituals Project. Tickets cost $12 and can be purchased from Jeremy Shaver, Director of Education and Family Ministries. This is an event that will touch your heart and move your spirit!</span>', '1', '1', '0', '1', '2007-09-03 19:13:43', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2007-09-03 19:12:59', '2007-10-15 02:00:00', '', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=', '1', '0', '2', '', '', '0', '0');
insert into `#__content` values ('131', 'Youth Sell E-Books To Earn Ski Trip', '', '<strong><span style=\"font-family: Arial; color: black\"></span></strong>  <span style=\"font-size: 12pt; font-family: Arial; color: black\">The Christ Congregational Church Youth Group will sell the familiar and fantastic e-Book this fall. The e-Book is a coupon book with discounts from dozens of area businesses. The books cost $10 and the church earns $6 from each sale. The books are a particular bargain for King Soopers shoppers! King Soopers is offering four $5 off coupons on a purchase of $50 or more. So, the books will pay for themselves in just two shopping trips! <br /></span>', '<span style=\"font-size: 12pt; font-family: Arial; color: black\">Proceeds from the books will help support youth programs at Christ Church, as well as the church itself. Youth who sell 15 books will also earn a free lift ticket for a Christ Church Youth Group ski trip! A sales table will be located in the Narthex every Sunday through September. Please support the church and youth group by purchasing an e-Book today!</span>', '1', '1', '0', '1', '2007-09-03 19:15:42', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2007-09-03 19:14:57', '2007-09-30 02:00:00', '', '', 'pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=', '1', '0', '1', '', '', '0', '1');
