insert into `#__sections` values ('5', 'About the UCC', 'AboutUCC', '', 'content', 'left', '<h3>About the UCC</h3><p>The United Church of Christ (UCC) is a mainline Protestant Christian denomination  and was formed in 1957 by the union of Pilgrims and Puritans from England, who formed Congregational churches and Swiss and southern German immigrants, who started the Reformed denomination.  </p><p>As of 2006, the United Church of Christ has approximately 1.2 million members and is composed of approximately 5,633 local congregations.</p><p>Learn more about our denomination in this <a href=\"http://i.ucc.org/StretchYourMind/WhatstheUCC/tabid/81/Default.aspx\" target=\"_blank\">six-part learning series</a> about the United Church of Christ </p>', '1', '0', '0000-00-00 00:00:00', '12', '0', '7', 'imagefolders=/');
insert into `#__sections` values ('6', 'About Us', 'AboutUs', '', 'content', 'left', '<h4>Our Church was founded in 1956.  It is a member of the United Church of Christ. Following Jesus’ lead, the United Church of Christ strives to keep doors open to all. By God’s grace—in the past and today—we do what needs to be done to be bold people of God’s welcome. <br> <a href=\"index.php?option=com_content&task=view&id=25&Itemid=32\">  > Mission Statement</a> <br>   <a href=\"index.php?option=com_content&task=section&id=17&Itemid=55\"> > Our History</a></h4>\r\n\r\n', '1', '0', '0000-00-00 00:00:00', '11', '0', '9', 'imagefolders=*1*');
